// client/src/components/QuestionForm.js
import React, { useState, useEffect } from 'react';
import Question from './Question';

function QuestionForm({ questions, onSubmit }) {
  const [answers, setAnswers] = useState({});
  const [errors, setErrors] = useState({});
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [lastUpdatedQuestion, setLastUpdatedQuestion] = useState(null);
  const [saveTimeout, setSaveTimeout] = useState(null);

  // Immediately save answers when they change
  useEffect(() => {
    if (lastUpdatedQuestion && Object.keys(answers).length > 0) {
      console.log('Answer changed for question:', lastUpdatedQuestion);
      console.log('Current answers state:', answers);
      
      // Clear any existing timeout
      if (saveTimeout) {
        clearTimeout(saveTimeout);
      }
      
      // Set a short delay to prevent too many submissions
      const timeoutId = setTimeout(() => {
        console.log('Auto-saving answers after question update');
        
        // Call onSubmit with the current answers and true to indicate partial submission
        onSubmit(answers, true);
        setLastUpdatedQuestion(null);
      }, 300); // Shorter delay for quicker saving
      
      setSaveTimeout(timeoutId);
    }
    
    // Cleanup timeout on unmount or when effect reruns
    return () => {
      if (saveTimeout) {
        clearTimeout(saveTimeout);
      }
    };
  }, [answers, lastUpdatedQuestion, onSubmit, saveTimeout]);

  const handleAnswerChange = (questionId, answer) => {
    console.log('Answer changed for question:', questionId, 'to:', answer);
    
    // Update the answers state
    setAnswers(prev => {
      const newAnswers = {
        ...prev,
        [questionId]: answer
      };
      
      // Track which question was just updated
      setLastUpdatedQuestion(questionId);
      
      return newAnswers;
    });
    
    // Clear error for this question if it exists
    if (errors[questionId]) {
      setErrors(prev => {
        const newErrors = { ...prev };
        delete newErrors[questionId];
        return newErrors;
      });
    }
  };

  const validateForm = () => {
    const newErrors = {};
    let isValid = true;
    
    // Check required fields
    questions.forEach(question => {
      if (question.obligatorisk === 'true' && (!answers[question['@_id']] || answers[question['@_id']] === '')) {
        newErrors[question['@_id']] = 'Dette felt er påkrævet';
        isValid = false;
      }
    });
    
    setErrors(newErrors);
    return isValid;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    
    if (!validateForm()) {
      return;
    }
    
    setIsSubmitting(true);
    
    // Submit all answers as final answers
    console.log('Submitting final answers:', answers);
    onSubmit(answers, false); // false indicates this is a final submission
    
    // Reset submission state after a short delay
    setTimeout(() => {
      setIsSubmitting(false);
    }, 1000);
  };

  return (
    <form onSubmit={handleSubmit} className="question-form">
      {questions.map(question => (
        <div key={question['@_id']} className="question-container">
          <Question
            question={question}
            onChange={(answer) => handleAnswerChange(question['@_id'], answer)}
          />
          
          {errors[question['@_id']] && (
            <div className="error-text">{errors[question['@_id']]}</div>
          )}
        </div>
      ))}
      
      <button 
        type="submit" 
        className="submit-button"
        disabled={isSubmitting}
      >
        {isSubmitting ? 'Indsender...' : 'Indsend besvarelser'}
      </button>
    </form>
  );
}

export default QuestionForm;
