// client/src/components/QuestionForm.js
import React, { useState } from 'react';
import Question from './Question';

function QuestionForm({ questions, onSubmit }) {
  const [answers, setAnswers] = useState({});
  const [errors, setErrors] = useState({});
  const [isSubmitting, setIsSubmitting] = useState(false);

  // Direct saving of answers without any delay
  const handleAnswerChange = (questionId, answer) => {
    console.log('Answer changed for question:', questionId, 'to:', answer);
    
    // Create updated answers object
    const updatedAnswers = {
      ...answers,
      [questionId]: answer
    };
    
    // Update state
    setAnswers(updatedAnswers);
    
    // Call onSubmit immediately for this individual answer
    console.log('Submitting answer immediately:', updatedAnswers);
    onSubmit(updatedAnswers, true); // true = partial submission
    
    // Clear error for this question if it exists
    if (errors[questionId]) {
      setErrors(prev => {
        const newErrors = { ...prev };
        delete newErrors[questionId];
        return newErrors;
      });
    }
  };

  const validateForm = () => {
    const newErrors = {};
    let isValid = true;
    
    // Check required fields
    questions.forEach(question => {
      if (question.obligatorisk === 'true' && (!answers[question['@_id']] || answers[question['@_id']] === '')) {
        newErrors[question['@_id']] = 'Dette felt er påkrævet';
        isValid = false;
      }
    });
    
    setErrors(newErrors);
    return isValid;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    
    if (!validateForm()) {
      return;
    }
    
    setIsSubmitting(true);
    
    // Submit all answers as final answers
    console.log('Submitting final answers:', answers);
    onSubmit(answers, false); // false indicates this is a final submission
    
    // Reset submission state after a short delay
    setTimeout(() => {
      setIsSubmitting(false);
    }, 1000);
  };

  return (
    <form onSubmit={handleSubmit} className="question-form">
      {questions.map(question => (
        <div key={question['@_id']} className="question-container">
          <Question
            question={question}
            onChange={(answer) => handleAnswerChange(question['@_id'], answer)}
          />
          
          {errors[question['@_id']] && (
            <div className="error-text">{errors[question['@_id']]}</div>
          )}
        </div>
      ))}
      
      <button 
        type="submit" 
        className="submit-button"
        disabled={isSubmitting}
      >
        {isSubmitting ? 'Indsender...' : 'Indsend besvarelser'}
      </button>
    </form>
  );
}

export default QuestionForm;
